  `update_date` datetime default NULL COMMENT '更新日期',
  `auto_form_db_id` varchar(32) default NULL COMMENT '数据源ID',
  `field_name` varchar(32) default NULL COMMENT '字段名',
  `field_text` varchar(50) default NULL COMMENT '字段文本',
  PRIMARY KEY  (`id`)
