mapper.select:
SELECT 

FROM mail_info a

WHERE a.id = #{id}

		SELECT 

		FROM mail_info a


			a.del_flag = #{DEL_FLAG_NORMAL}
            and own_id = #{ownId}
            and state = #{state}



				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM mail_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mail_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

