SELECT 

FROM oa_person_define_table_column a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.table_id = #{tableId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM oa_person_define_table_column a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
